import React, { useState, useEffect } from "react";
import { Client } from "./Client";
import { Editor } from "./Editor";
import codeMirror from "codemirror";
import "codemirror/mode/javascript/javascript";
import "codemirror/theme/dracula.css";
import "codemirror/addon/edit/closetag";
import "codemirror/addon/edit/closebrackets";
import "codemirror/lib/codemirror.css";

export const Editorpage = () => {
  const [connect1, setconnect1] = useState([
    {
      stockeId: 1,
      username: "babu k",
    },
    {
      stockeId: 2,
      username: "sasanka r",
    },
    {
      stockeId: 3,
      username: "ram k",
    },
  ]);

  useEffect(() => {
    async function init() {
      codeMirror.fromTextArea(document.getElementById("RTeditor"), {
        mode: { name: "javascript", json: true },
        theme: "dracula",
        autoCloseTags: true,
        autoCloseBrackets: true,
        lineNumbers: true,
      });
    }
    console.log("edito");
    init();
  }, []);
  return (
    <>
      <div className="mainWrap">
        <div className="leftSide">
          <div className="sideInner">
            <div className="logo">
              <h2 className="logo_h2">
                real-time <span className="logoName">code editor</span>
              </h2>
            </div>
            <h3 className="conn_h3">connected</h3>
            <div className="connected_list">
              {connect1.map((client) => (
                <Client key={client.stockeId} username={client.username} />
              ))}
            </div>
          </div>
          <button className="btn btn_copy">copy Room ID</button>
          <button className="btn btn_leave">Leave</button>
        </div>

        <div className="editor">
          <Editor />
        </div>
      </div>
    </>
  );
};
