import React,{useState} from "react";
import {v4 as uuid4} from "uuid"

export const Home = () => {
  const [input, setinput] = useState({
    id:"",
    Uname:"",

  })
  const inputval = (event)=>{
    event.preventDefault();
    let value= event.target.value;
    let name= event.target.name;
    setinput({
      [name]:value
    })
    console.log(input);
  }

  const newRoom = (e)=>{
    e.preventDefault();
    const uniqueId = uuid4()
    console.log(uniqueId)
    inputval();
  }
  return (
    <>
      <div className="homePage">
        <div className="fromWrapper">
            <h2 className="logo">real-time <span className="logoName">code editor</span></h2>
            <h4 className="lable">Enter invitation room id.</h4>
            <div className="ints_here">
                <input type="text" name="userId" value={input.id} onChange={inputval} placeholder="room id" className="input_box" />
                
                <input type="text" name="username" value={input.Uname} onChange={inputval} placeholder="user name" className="input_box" />
                
                <button className="btn btn_1">Join</button>
                

                <span className="createInfo">
                    It you don't have an invitation then create &nbsp;
                    <a href="/" onClick={newRoom} className="createNew">new Room.</a>
                </span>
            </div>
        </div>
        <footer>
          <h4 className="footer">
              build with ðŸ’›&nbsp;
             <span className="name"> sasanka roy .</span>
          </h4>
      </footer>
      </div>
      
    </>
  );
};
